import React from "react";
import axios from "axios";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
  Input,
  FormGroup,
  Button,
  Label,
} from "reactstrap";
// reactstrap components
//import { Card, CardHeader, CardBody, Row, Col } from "reactstrap";
import TableRowUser from "components/TableRow/TableRowUser.js";
import { ToastContainer, toast } from "react-toastify";
import { Redirect } from "react-router-dom";
class Icons extends React.Component {
  constructor(props) {
    super(props);
    this.onChangeValue = this.onChangeValue.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.onSelectValue = this.onSelectValue.bind(this);
    this.onSubmitEdit = this.onSubmitEdit.bind(this);
    this.onChangeNewName= this.onChangeNewName.bind(this);
    this.onSubmitEditUser=this.onSubmitEditUser.bind(this);
    this.state = {
      data1: [],
      dataedituser: [],
      code:"",
      firstsname: "",
      lastname: "",
      sex: "",
      dob: "",
      phonenumber: "",
      email: "",
      address: "",
      username: "",
      password: "",
      trangthai: false,
      trangthaiedit: false,
      trangthaiedit1: false,
      newSex:"",
      newfirst:"",
      newlast:"",
      newsex:"",
      newdob:"",
      newphone:"",
      newaddress:"",
      newpassword: "",
    };
  }

  // thêm user
  onChangeValue(event) {
    var name = event.target.name;
    var value = event.target.value;
    this.setState({
      [name]: value,
    });
  }
  onSelectValue(event) {
    this.setState({
      role: event.target.select,
    });
  }
  onSubmit(event) {
    event.preventDefault();
    if(this.state.iduser!==''&&this.state.firstname!==''&&this.state.lastname!==''
    &&this.state.sex!==''&&this.state.dob!==''&&this.state.phonenumber!==''&&this.state.address!==''
    &&this.state.username!==''&&this.state.password!=='')
    {
      const formData = {
        code: this.state.code,
        firstname: this.state.firstname,
        lastname: this.state.lastname,
        sex: this.state.sex,
        birthday: this.state.dob,
        phone: this.state.phonenumber,
        email: this.state.email,
        address: this.state.address,
        username: this.state.username,
        password: this.state.password,
      };
  
      console.log(formData);
      axios
        .post("/employee", formData)
        .then((res) => {
          if (res.data.success === true) {
            toast.success("add user success");
            this.setState({
              data1:[...this.state.data1,formData]
            })
          }
          else
          {
            toast.error("username already exists!");
          }
          
        })
        .catch((err) => {
          toast.error(`Upload Fail with status: ${err.statusText}`);
        });
    }
    else
    {
      alert('please fill out all information');
    }
    
  }
  // hiển thị list user
  componentDidMount() {
    axios
      .get("/employee", {
        headers: {
          "Content-Type": "application/json",
          Authorization: JSON.parse(localStorage.getItem("authorization")),
        },
      })
      .then((response) => {
        this.setState({ data1: response.data.employees });
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  tabUser = () =>
    this.state.data1.map((object, i) => (
      <TableRowUser
        onView={() => this.onClickEdit(object)}
        obj={object}
        key={i}
        onChangeTT={() => this.thayDoiTrangThaiEdit()}
        onCHangeEdit={() => this.thayDoiTrangThaiEdit1()}
      />
    ));

  // Check trạng thái thay đổi button
  checkNut() {
    if (this.state.trangthai === false) {
      return (
        <Button onClick={() => this.thayTrangThai()} size="sm" md="2">
          +
        </Button>
      );
    } else {
      return (
        <Button onClick={() => this.thayTrangThai()} size="sm" md="2">
          -
        </Button>
      );
    }
  }
  // click thay đổi trạng thái
  thayTrangThai = () => {
    this.setState({
      trangthai: !this.state.trangthai,
    });
  };
  // click trang thái thay đổi form
  hienThiForm() {
    if (this.state.trangthai === true) {
      return (
          <Card className="abc">
            <CardHeader>
              <CardTitle>
                <Label tag="h4">Create Employee</Label>
              </CardTitle>
            </CardHeader>
            <CardBody>
              <FormGroup row>
                <Label sm={3}>Code</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="code"
                    id=""
                    placeholder="Input code"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>FirstName</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="firstname"
                    id=""
                    placeholder="Input firstname"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>LastName</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="lastname"
                    id=""
                    placeholder="Input lastname"
                  />
                </Col>
              </FormGroup>
              <FormGroup tag="fieldset" row>
                <Label sm={4}>Sex</Label>
                <Col sm={8}>
                  <FormGroup row check>
                    <Label sm={4} check>
                      <Input
                        type="radio"
                        name="sex"
                        value="Male"
                        checked={this.state.sex === "Male"}
                        onChange={this.onChangeValue}
                      />{" "}
                      Male
                    </Label>
                    <Label sm={4} check>
                      <Input
                        type="radio"
                        name="sex"
                        value="Female"
                        checked={this.state.sex === "Female"}
                        onChange={this.onChangeValue}
                      />{" "}
