import React from "react";
import axios from 'axios';
// react plugin for creating notifications over the dashboard
import NotificationAlert from "react-notification-alert";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import TableRow from "components/TableRow/TableRowDoc.js";
import {Redirect } from "react-router-dom";
import TableRowSearch from "components/TableRow/TableRowSearch.js";
// reactstrap components
import { 
  Card,
  CardBody,
  Row,
  Label,
  Input,
  FormGroup,
  Table,
  CardTitle,
  Col
} from "reactstrap";
import CardHeader from "reactstrap/lib/CardHeader";
import Button from "reactstrap/lib/Button";

class Notifications extends React.Component {

  constructor(props) {
    super(props);
    this.onChangeValue = this.onChangeValue.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.onSubmitTheme = this.onSubmitTheme.bind(this);
    this.onChangeValueTheme = this.onChangeValueTheme.bind(this);
    this.state = {
      data:[] ,
      dataserch:[],
      datatheme:[],
      trangthai:false,
      trangthaiserarch:false,
      idsubjectView:"",
      datadoc:[],
      code: "",
      firstsname: "",
      lastname: "",
      sex: "",
      dob: "",
      phonenumber: "",
      email:"",
      address: "",
      totalmoney: "",
      visit: "",
      level: "",
      purchases: "",
    }
  }
  // componentDidMount() {
  //   axios.get('/api/doc/all')
  //       .then(response => {
  //           console.log(response.data.listdoc);
  //           this.setState({data: response.data.listdoc});
  //       })
  //       .catch(function (error) {
  //           console.log(error);
  //       })
  // }

  onSubmitTheme(event){
    event.preventDefault();
    
    if(this.state.idsubjectView==='')
    {
      
      this.state.idsubjectView=this.getFirstTheme();
    }
   
    event.preventDefault();
    var idsubject = this.state.idsubjectView
    
    axios.get('/api/doc/subject/'+idsubject)
    .then((res)=>{
      console.log(res.data);
      this.setState({
        datadoc:res.data.lstDoc
      });
    })
  }
  onChangeValue(event){
    this.setState({
      key:event.target.value
    });
  }
  onChangeValueTheme(event){
    this.setState({
      idsubjectView:event.target.value
    });
  }
  onSubmit(event) {
    event.preventDefault();
    if(
      this.state.code!==''&&
      this.state.firstname!==''&&
      this.state.lastname!==''&&
      this.state.sex!==''&&
      this.state.dob!==''&&
      this.state.phonenumber!==''&&
      this.state.address!==''&&
      this.state.email!==''&&
      this.state.totalmoney!==''&&
      this.state.visit!==''&&
      this.state.purchases!==''&&
      this.state.state.level!=='')
    {
      const formData = {
        code: this.state.code,
        firstname: this.state.firstname,
        lastname: this.state.lastname,
        sex: this.state.sex,
        dob: this.state.dob,
        phonenumber: this.state.phonenumber,
        address: this.state.address,
        email:this.state.email,
        
      };
  
      console.log(formData);
      axios
        .post("/api/user/register", formData)
        .then((res) => {
          if (res.data.success === true) {
            toast.success("add user success");
            setTimeout(function () {
              window.location.reload(1);
            },1000);
          }
          else
          {
            toast.error("username already exists!");
          }
          
        })
        .catch((err) => {
          toast.error(`Upload Fail with status: ${err.statusText}`);
        });
    }
    else
    {
      alert('please fill out all information');
    }
    
  }
  tabRow=()=>this.state.datadoc.map((object,i)=>(
    <TableRow 
    obj={object} key={i}
    />
  ))
  
  tabRowSearch=()=>this.state.dataserch.map((object,i)=>(
      <TableRowSearch obj={object} key={i}/>
    )
  )
  checkNut(){
    if(this.state.trangthai===false){
      return <Button onClick={()=>this.thayTrangThai()} size="sm" md="2">+</Button>;    
    }
    else{
      return <Button onClick={()=>this.thayTrangThai()} size="sm" md="2">-</Button>
    }
  }
  thayTrangThai=()=>{
    this.setState({
      trangthai: !this.state.trangthai,
      dataserch:[]
    });
  }
  hienThiForm() {
    if (this.state.trangthai === true) {
      return (
          <Card className="abc">
            <CardHeader>
              <CardTitle>
                <Label tag="h4">Create Employee</Label>
              </CardTitle>
            </CardHeader>
            <CardBody>
              <FormGroup row>
                <Label sm={3}>Code</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="code"
                    id=""
                    placeholder="Input code"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>FirstName</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="firstname"
                    id=""
                    placeholder="Input firstname"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>LastName</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="lastname"
                    id=""
                    placeholder="Input lastname"
                  />
                </Col>
              </FormGroup>
              <FormGroup tag="fieldset" row>
                <Label sm={4}>Sex</Label>
                <Col sm={8}>
                  <FormGroup row check>
                    <Label sm={4} check>
                      <Input
                        type="radio"
                        name="sex"
                        value="Male"
                        checked={this.state.sex === "Male"}
                        onChange={this.onChangeValue}
                      />{" "}
                      Male
                    </Label>
                    <Label sm={4} check>
                      <Input
                        type="radio"
                        name="sex"
                        value="Female"
                        checked={this.state.sex === "Female"}
                        onChange={this.onChangeValue}
                      />{" "}
                      Female
                    </Label>
                  </FormGroup>
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>DOB</Label>
                <Col sm={9}>
                  <Input
                    type="date"
                    name="dob"
                    id=""
                    placeholder="date placeholder"
                    onChange={this.onChangeValue}
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Phone</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="phonenumber"
                    id=""
                    placeholder="Input phone number"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Address</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="address"
                    id=""
                    placeholder="Input address"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Email</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="email"
                    id=""
                    placeholder="Input email"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Money</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="totalmoney"
                    id=""
                    placeholder="Input money"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Visit</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="visit"
                    id=""
                    placeholder="Input visit"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Level</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="level"
                    id=""
                    placeholder="Input level"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Purchases</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="purchases"
                    id=""
                    placeholder="Input Purchases"
                  />
                </Col>
              </FormGroup>
              <Button onClick={this.onSubmit} color="primary">
                Add user
              </Button>
              <Button onClick={() => this.thayTrangThai()}>Cancel</Button>
            </CardBody>
          </Card>
      );
    }
  }
  hienThiFormMain(){
    if(this.state.trangthai===false){
      return(
        <Col md="12">
            <Card className="abc">
              <CardHeader>
                <FormGroup row className="haiben">
                  <Label md="10" tag="h6">
                    List user
                  </Label>
                  {this.checkNut()}
                </FormGroup>
              </CardHeader>
              <CardBody>
                <Table className="tablesorter" striped>
                  <thead className="text-primary">
                    <tr>
                      <th>Code</th>
                      <th>First Name</th>
                      <th>Last Name</th>
                      <th>Sex</th>
                      <th>Date of birth</th>
                      <th>Phone Number</th>
                      <th>Email</th>
                      <th>Address</th>
                      <th>Total Money</th>
                      <th>Visit</th>
                      <th>Level</th>
                      <th>Purchases</th>
                      <th>action</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>kh1</td>
                      <td>Nguyn</td>
                      <td>Thng</td>
                      <td>Nam</td>
                      <td>01/01/1999</td>
                      <td>0979797979</td>
                      <td>admin@gmail.com</td>
                      <td>01 V Vn Ngn</td>
                      <td>32000000</td>
                      <td>15</td>
                      <td>Tim nng</td>
                      <td>25</td>
                      <td>
                      <Button size="sm" onClick={this.delete} color="danger">
                                <svg xmlns="http://www.w3.org/2000/svg" 
                                  className="h-6 w-6" fill="none" 
                                  viewBox="0 0 24 24" 
                                  stroke="currentColor"
                                  style={{width:'20px',height:'20px',color:'#fff'}}
                                >
                                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                                </svg>
                              </Button>
                              <Button size="sm" onClick={this.download1} color="success">
                                <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" style={{width:'20px',height:'20px',color:'#fff'}}>
                                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                                </svg>
                              </Button>
                      </td>
                    </tr>
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
      );
    }
  }

  componentDidMount() {
    axios.get('/api/theme/alltheme')
        .then(response => {
            console.log(response.data.listtheme);
            this.setState({datatheme: response.data.listtheme});
        })
        .catch(function (error) {
            console.log(error);
        })
  }
  tabRowSearch=()=>this.state.dataserch.map((object,i)=>(
    <TableRowSearch obj={object} key={i}/>
  )
)
  getFirstTheme=()=>{
    
    for(let i in this.state.datatheme){
      return this.state.datatheme[i].idtheme;
    }
    return "";
  }
  render() {
    if (!localStorage.getItem('authorization')) return <Redirect to="/login" />
    return (
        <div style={{background: 'rgba(203,203,210,.15)'}} className="content">
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Row>
            <Col md="4">
                {this.hienThiForm()}
            </Col>
            {this.hienThiFormMain()}
          </Row>
        </div>
    );
  }
}

export default Notifications;
