                <td>
                    {this.props.obj.birthday}
                </td>
                <td>
                    {this.props.obj.phone}
                </td>
                <td>
                    {this.props.obj.email}
                </td>
                <td>
                    {this.props.obj.address}
                </td>
                <td>
                    {this.props.obj.totalmoney}
                </td>
                <td>
                    {this.props.obj.visit}
