import React, { Component } from 'react';

import { 
    Card,
    CardBody,
    Row,
    Label,
    Input,
    FormGroup,
    Table,
    Col,
    CardTitle
} from "reactstrap";
import Button from "reactstrap/lib/Button";
import CardHeader from "reactstrap/lib/CardHeader";
class Tchc extends Component {
    constructor(props) {
        super(props);
        this.state = {
          trangthai:true
        }
    }

    

    render() {
        console.log('abc',this.state);
        return (
            <div style={{background: 'LightCyan'}} className="content">
                <Row>
                    <Col md="8">
                        <Card className="abc">               
                            <CardHeader>
                                <FormGroup row>
                                    <Label md="4"  sm={6} tag="h6">Danh sách yêu cầu chỉnh sửa</Label>
                                </FormGroup>
                      </CardHeader>   
                        <CardBody>
                          <Table striped>
                              <thead>
                                <tr>
                                  <th>ID</th>
                                  <th>Lý Do</th>
                                  <th>Nội dung đề suất</th>
                                  <th>Ghi Chú</th>
                                  <th>Action</th>
                                </tr>
                              </thead>
                              <tbody>
                                <tr>
                                  <td>1</td>
                                  <td>Hư quạt</td>
                                  <td>Thay quạt</td>
                                  <td>Cần gấp</td>
                                  
                                  <td>
                                    <Button size="sm" onClick={this._doiTrangThai} color="success">Điều Phối</Button>
                                  </td>
                                </tr>
                                <tr>
                                  <td>2</td>
                                  <td>Hư bàn phím</td>
                                  <td>Thay bàn phím</td>
                                  <td>Cần gấp</td>
                                  
                                  <td>
                                      <Button size="sm" onClick={this._doiTrangThai} color="success">Điều Phối</Button>
                                  </td>
                                </tr>
                                <tr>
                                  <td>3</td>
                                  <td>Hư chuột</td>
                                  <td>Thay chuột</td>
                                  <td>Không gấp</td>
                                  
                                  <td>
                                      <Button size="sm" onClick={this._doiTrangThai} color="success">Điều Phối</Button>
                                  </td>
                                </tr>
                                <tr>
                                  <td>5</td>
                                  <td>Hư màn hình</td>
                                  <td>Thay màn hình</td>
                                  <td>Cần gấp</td>
                                  <td>
                                      <Button size="sm" onClick={this._doiTrangThai} color="success">Điều Phối</Button>
                                  </td>
                                </tr>
                              </tbody>
                            </Table>
                      </CardBody>
                    </Card>
                  </Col>
                    <Col md="4">
                        {this._viewEdit}
                    </Col>
                    
                </Row>
            </div>
        );
    }
    _doiTrangThai = () =>{
      this.setState({
          trangthai: !this.state.trangthai
      });
  }
  _viewEdit(){
      // if(this.state.trangthai === true){
          return(
          //     <Card>
          //       <CardHeader>
          //         <CardTitle tag="h3">UPLOAD DOCUMENT</CardTitle>
          //       </CardHeader>
          //       <CardBody>
          //         <FormGroup>
          //           <Label tag="h5">ID Document</Label>
          //           <Input pattern="[0-9]*" value={this.state.idDoc} onChange={this.onchangValue} type="text" name="idDoc" id="" placeholder="Input id document" />
                
          //         </FormGroup>
          //         <FormGroup>
          //           <Label tag="h5">Authorname</Label>
          //           <Input value={this.state.authorname} onChange={this.onchangValue} type="text" name="authorname" id="" placeholder="Input authorname" />
                
          //         </FormGroup>
          //         <FormGroup>
          //           <Label tag="h5">Note</Label>
          //           <Input value={this.state.note} onChange={this.onchangValue} type="text" name="note" id="" placeholder="Input note" />
          //         </FormGroup>
          //       {/* <FormGroup>
          //         <Label tag="h5">Category</Label>
          //         <Input defaultValue={this.state.datalist.idtheme} onChange={this.onchangValue} type="select" name="idsubject" id="">
          //           {this.state.datalist.map((list,i) => (
          //               <option key={i} value={list.idtheme}>{list.name}</option>
          //           ))}
          //         </Input>
          //       </FormGroup> */}
          //         <Button >Xác Nhận</Button>
          //         <Button onClick={this._doiTrangThai()}>Thoát</Button>
          //    </CardBody>              
          // </Card>
          <>
          </>
          );

      
  }
   
}

export default Tchc;
